mapper.update:
UPDATE MEMBER
SET NICKNAME      = #{nickname},
    EMAIL         = #{email},
    PASSWORD      = #{password},
    PROFILE_IMAGE = #{profile_image}
WHERE MEMBER_ID = #{member_id}

UPDATE MEMBER
SET LOGIN_ID = #{login_id},
    NICKNAME = #{nickname},
    STATUS   = #{status}
WHERE MEMBER_ID = #{member_id}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE LOGIN_ID = #{login_id}
